import { useState } from 'react';
import { NavLink } from 'react-router-dom';
import * as Auth from '../utils/Auth';
import { useNavigate } from 'react-router-dom';

function Register(props) {
  const [formValue, setFormValue] = useState({
    email: '',
    password: ''
  });

  const navigate = useNavigate();

  function handleChange(e) {
    const { name, value } = e.target;

    setFormValue({
      ...formValue,
      [name]: value
    });
  }

  function handleSubmit(e) {
    //e.preventDefault();
    const { email, password } = formValue;
    Auth.register(formValue.password, formValue.email)
    .then((res) => {
      {props.handleSignUp()}
      {props.onHandleSubmit()}
      navigate('/sign-in', {replace: true});
    })
    .catch((res) => {
      {props.onHandleSubmit()}
    })
  }

  return(
    <div className="auth">
      <h2 className="auth__title">Регистрация</h2>
      <form className="auth__form" onSubmit={handleSubmit}>
        <input
          className='auth__input'
          value={formValue.email}
          onChange={handleChange}
          placeholder="Email"
          type="email"
          name="email"
          id="email"
          required
        />
        <input
          className='auth__input'
          value={formValue.password}
          onChange={handleChange}
          placeholder="Пароль"
          type="password"
          name="password"
          id="password"
          required
        />
        <button className='auth__submit-button' type="submit" onSubmit={handleSubmit}>Зарегистрироваться</button>
      </form>
      <NavLink className="auth__link" to='/sign-in'>Уже зарегистрированы? Войти</NavLink>
    </div>
  );
}

export default Register;
