function InfoTooltip(props) {
  return(
    <div className={`popup popup_name_form ${props.isOpen ? `popup_opened` : ""}`}>
      <div className="popup__container popup__container_type_sign-up">
        <div className={`popup__sign-up ${props.isSignUp ? 'popup__sign-up_type_success' : 'popup__sign-up_type_failure'}`}/>
        <h2 className="popup__title">
          {props.isSignUp ? 'Вы успешно зарегистрировались!' : 'Что-то пошло не так! Попробуйте еще раз.'}
        </h2>
      </div>
    </div>
  );
}
